--CREATE TABLE CCSD_BATCHFINANCE (BFTB_ID BIGINT AUTO_INCREMENT NOT NULL UNIQUE, BFTB_BATCHFEE BIGINT NOT NULL, BFTB_CLASSBATCH_FK BIGINT NOT NULL UNIQUE, PRIMARY KEY (BFTB_ID));
CREATE TABLE CCSD_CLASSBATCH (CBTB_ID BIGINT AUTO_INCREMENT NOT NULL UNIQUE, CBTB_BATCHNAME VARCHAR(255) NOT NULL, CBTB_BATCHSTD INTEGER NOT NULL, CBTB_BATCHSTREAM VARCHAR(255) NOT NULL,CBTB_BATCHFEE BIGINT NOT NULL, PRIMARY KEY (CBTB_ID),CONSTRAINT UNIQUE(CBTB_BATCHNAME,CBTB_BATCHSTD,CBTB_BATCHSTREAM));
CREATE TABLE CCSD_EMPLOYEE (EMTB_ID BIGINT AUTO_INCREMENT NOT NULL UNIQUE, EMTB_ADDRESS VARCHAR(255), EMTB_CONTACTNUMBER VARCHAR(255) NOT NULL, EMTB_JOININGDATE DATE NOT NULL, EMTB_NAME VARCHAR(255) NOT NULL, EMTB_SALARY BIGINT NOT NULL, EMTB_USERID_FK BIGINT NOT NULL UNIQUE, PRIMARY KEY (EMTB_ID));
CREATE TABLE CCSD_EMPLOYEEFINANCE (EFTB_ID BIGINT AUTO_INCREMENT NOT NULL UNIQUE, EFTB_AMOUNTPAID BIGINT NOT NULL, EFTB_RECEIVEDDATE DATE NOT NULL, EFTB_EMPLOYEEID_FK BIGINT, PRIMARY KEY (EFTB_ID));
CREATE TABLE CCSD_EXPENSE (EXTB_ID BIGINT AUTO_INCREMENT NOT NULL UNIQUE, EXTB_EXPENSETYPE VARCHAR(255) NOT NULL, EXTB_EXPENSEAMOUNT BIGINT NOT NULL, EXTB_EXPENSEDATE DATE NOT NULL, EXTB_EXPENSEINFO VARCHAR(255), SFTB_USERID_FK BIGINT, PRIMARY KEY (EXTB_ID));
CREATE TABLE CCSD_STUDENT (SDTB_ID BIGINT AUTO_INCREMENT NOT NULL UNIQUE, SDTB_ADDRESS VARCHAR(255), SDTB_AGE INTEGER NOT NULL, SDTB_CONSESATION BIGINT, SDTB_CONNUMBER INTEGER NOT NULL, SDTB_CONPNUMBER INTEGER NOT NULL, SDTB_NAME VARCHAR(255) NOT NULL, SDTB_OTHERINFO VARCHAR(255), SDTB_PARENTNAME VARCHAR(255) NOT NULL, SDTB_SCHOOLNAME VARCHAR(255),SDTB_STUDENTUSERNAME VARCHAR(255) NOT NULL, SDTB_CLASSBATCH_FK BIGINT, PRIMARY KEY (SDTB_ID),CONSTRAINT UNIQUE(SDTB_NAME,SDTB_STUDENTUSERNAME));
CREATE TABLE CCSD_STUDENTFINANCE (SFTB_ID BIGINT AUTO_INCREMENT NOT NULL UNIQUE, SFTB_AMOUNT BIGINT NOT NULL, SFTB_FRCEIVEDDATE DATE NOT NULL, SFTB_USERID_FK BIGINT NOT NULL, SFTB_STUDENTID_FK BIGINT, PRIMARY KEY (SFTB_ID));
CREATE TABLE CCSD_USER (USTB_ID BIGINT AUTO_INCREMENT NOT NULL UNIQUE, USTB_USERNAME VARCHAR(255) NOT NULL, USTB_USERPASSWORD VARCHAR(255) NOT NULL, USTB_USERROLE VARCHAR(255) NOT NULL, PRIMARY KEY (USTB_ID),CONSTRAINT UNIQUE(USTB_USERNAME,USTB_USERPASSWORD));
--28-05-2013
CREATE TABLE CCSD_ATTENDANCE (STAT_ID BIGINT AUTO_INCREMENT NOT NULL UNIQUE,STAT_ABSENTDATE DATE NOT NULL,STAT_STUDENTID_FK BIGINT, PRIMARY KEY (STAT_ID),CONSTRAINT UNIQUE(STAT_ABSENTDATE,STAT_STUDENTID_FK));

--ALTER TABLE CCSD_BATCHFINANCE ADD CONSTRAINT FK_CCSD_BATCHFINANCE_BFTB_CLASSBATCH_FK FOREIGN KEY (BFTB_CLASSBATCH_FK) REFERENCES CCSD_CLASSBATCH (CBTB_ID);
ALTER TABLE CCSD_EMPLOYEE ADD CONSTRAINT FK_CCSD_EMPLOYEE_EMTB_USERID_FK FOREIGN KEY (EMTB_USERID_FK) REFERENCES CCSD_USER (USTB_ID);
ALTER TABLE CCSD_EMPLOYEEFINANCE ADD CONSTRAINT FK_CCSD_EMPLOYEEFINANCE_EFTB_EMPLOYEEID_FK FOREIGN KEY (EFTB_EMPLOYEEID_FK) REFERENCES CCSD_EMPLOYEE (EMTB_ID);
ALTER TABLE CCSD_EXPENSE ADD CONSTRAINT FK_CCSD_EXPENSE_SFTB_USERID_FK FOREIGN KEY (SFTB_USERID_FK) REFERENCES CCSD_USER (USTB_ID);
ALTER TABLE CCSD_STUDENT ADD CONSTRAINT FK_CCSD_STUDENT_SDTB_CLASSBATCH_FK FOREIGN KEY (SDTB_CLASSBATCH_FK) REFERENCES CCSD_CLASSBATCH (CBTB_ID);
ALTER TABLE CCSD_STUDENTFINANCE ADD CONSTRAINT FK_CCSD_STUDENTFINANCE_SFTB_STUDENTID_FK FOREIGN KEY (SFTB_STUDENTID_FK) REFERENCES CCSD_STUDENT (SDTB_ID);
--28-05-2013
ALTER TABLE CCSD_ATTENDANCE ADD CONSTRAINT FK_CCSD_ATTENDANCE_STAT_STUDENTID_FK FOREIGN KEY (STAT_STUDENTID_FK) REFERENCES CCSD_STUDENT (SDTB_ID);
